mapper.select:
select count(*) as "board_count" from (select * from board b left join member m on b.board_writer_no=m.member_no where b.board_type=1 and b.board_state!=0
and (b.board_content like '%'||#{keyword}||'%' or b.board_title like '%'||#{keyword}||'%' or m.member_name like '%'||#{keyword}||'%')

	and b.board_category=#{inputValue}

)

select * from (select rownum rn, v.* from (select b.*, m.member_image, m.member_name, nvl(cnt,0) cnt from board b left join member m on b.board_writer_no = m.member_no left join (select board_no, count(*) cnt from review left join board on review_board_no=board_no group by board_no) ct on b.board_no=ct.board_no  where b.board_type=1 and board_state!=0 and (b.board_content like '%'||#{keyword}||'%' or b.board_title like '%'||#{keyword}||'%' or m.member_name like '%'||#{keyword}||'%')

	and b.board_category=#{inputValue}

 order by b.board_no desc) v) z where z.rn between ((#{pageNum} - 1) * 4 + 1) and (#{pageNum} * 4)
